mapper.select:
SELECT ID
FROM USER
WHERE ID=#{id}

select sub_name, sub_code, edu_code
from subject
where edu_code=#{edu_code}

select edu_name
from education
where edu_code=#{edu_code}

select sub_name
from subject
where sub_code=#{sub_code}

select edu_code
from subject
where sub_code=#{sub_code}

select id, name, auth
from user
where id=#{studentid}

SELECT ID, regidate
FROM USER
WHERE name=#{name}
and phone=#{phone}

SELECT ID
FROM USER
WHERE ID=#{id}
and phone=#{phone}

mapper.update:
update user
set password=#{password}
where id=#{id}

mapper.select:
select id, password, name, gender, email, birth, address, facename, newfacename, phone, phone_public, jointype, auth, breakaway, regidate
from user
where id=#{id} and password=#{password}

mapper.update:
update user
set password=#{password}, name=#{name}, gender=#{gender}, email=#{email}, address=#{address}, 
	facename=#{facename}, phone=#{phone}
where id=#{id}

mapper.select:
select sub_code, edu_code, edu_name, sub_name, class_grade, student_id, student_grade, student_ranks, sub_total from ( select G.SUB_CODE, G.EDU_CODE, G.SUB_NAME, I.NAME, G.CLASS_GRADE, G.STUDENT_ID, G.STUDENT_GRADE, e.edu_name, (ROW_NUMBER() OVER (PARTITION BY SUB_CODE ORDER BY STUDENT_GRADE DESC))as student_ranks , K.sub_total FROM GRADE G INNER JOIN SUBJECT S ON G.SUB_CODE = S.SUB_CODE inner join education e on s.edu_code = e.edu_code INNER JOIN USER I ON G.STUDENT_ID = I.ID inner join ( select SUB_CODE, COUNT( SUB_CODE ) as sub_total FROM GRADE group by SUB_CODE ) K on G.SUB_CODE = K.SUB_CODE ) as A where 1=1 and STUDENT_ID=#{receiver}
and edu_name like concat('%', #{search}, '%')
and sub_name like concat('%', #{search}, '%')
